<script>
import { gsap } from 'gsap';

export default {
  mounted() {
    this.logoAnimation();
    gsap.to('.page-loader', { opacity: 1, duration: 0.5 });
    this.fadeOut();
  },
  methods: {
    fadeOut() {
      setTimeout(() => {
        gsap.to('.page-loader', { opacity: 0, duration: 0.5, onComplete: this.removeLoader });
      }, 1500);
    },
    removeLoader() {
      this.$el.remove();
    },
    logoAnimation() {
      // Animatie voor het linkerdeel
      gsap.fromTo('.logo-top', { x: -100, opacity: 0 }, { x: 0, opacity: 1, duration: 0.5, ease: 'power2.out' });
      // Animatie voor het rechterdeel
      gsap.fromTo('.logo-bottom', { x: 100, opacity: 0 }, { x: 0, opacity: 1, duration: 0.5, ease: 'power2.out' });
    },
  },
};
</script>

